sys_script_include.script:
var ImageMapHelper = Class.create();
ImageMapHelper.prototype = {
	initialize: function() {
	},


	generateGuid: function () {
		function s4() {
			return Math.floor((1 + Math.random()) * 0x10000)
				.toString(16)
				.substring(1);
		}
		return s4() + s4() + '-' + s4() + '-' + s4() + '-' + s4() + '-' + s4() + s4() + s4();
	},

	getMapForMacroVar: function (sys_id, name) {

		var gr = new GlideRecord('x_pisn_imagemap_map');

		if (sys_id) {
			gr.addQuery('macro_variable', sys_id);
		} else {
			gr.addQuery('macro_variable.name', name);
		}

		gr.setLimit(1);
		gr.query();

		if (gr.hasNext()) {

			gr.next();
			return new Map(gr);

		}

		return false;

	},

	type: 'ImageMapHelper'
};
